export class CerebralComponent {
  _cdr:any;
  _controller:any;

  constructor(cdr:any, controller:any) {
    if (!cdr || !cdr._view) {
      throw new Error(
        'You did not pass a "ChangeDetectorRef" to the Cerebral Component parent class'
      )
    }
    if (!controller || !(typeof controller.flush === 'function')) {
      throw new Error(
        'You did not pass a "ControllerService" to the Cerebral Component parent class'
      )
    }

    this._cdr = cdr
    this._controller = controller
  }
  ngOnDestroy() {}
  ngOnInit() {}
  ngAfterViewInit() {}
}
