import { convertObjectWithTemplates } from 'cerebral/tags'

function onValueFactory(path, signal, options = {}) {
  function onValue({ firebase, resolve }) {
    firebase.onValue(
      resolve.value(path),
      resolve.path(signal),
      convertObjectWithTemplates(options, resolve, 'firebase.onValue')
    )
  }

  return onValue
}

export default onValueFactory
