import { convertObjectWithTemplates, processResponse } from '../utils'

function httpGetFactory(url, query = {}, options) {
  function httpGet({ http, path, resolve }) {
    return processResponse(
      http.get(
        resolve.value(url),
        convertObjectWithTemplates(query, resolve),
        options
      ),
      path
    )
  }

  return httpGet
}

export default httpGetFactory
