import { convertObjectWithTemplates, processResponse } from '../utils'

function httpPostFactory(url, body = {}, options = {}) {
  function httpPost({ http, path, resolve }) {
    return processResponse(
      http.post(
        resolve.value(url),
        convertObjectWithTemplates(body, resolve),
        options
      ),
      path
    )
  }

  return httpPost
}

export default httpPostFactory
