function connect(HOC, dependencies, mergeProps) {
  return (component) => HOC(dependencies, mergeProps, component)
}

export const connectAndMergeFactory = (HOC) => (dependencies) => (mergeProps) =>
  connect(HOC, dependencies, mergeProps)

export default (HOC) => (dependencies) => connect(HOC, dependencies)

export const decoratorFactory = (HOC) => (dependencies) => (component) =>
  connect(HOC, dependencies)(component)
