import * as React from 'react'
import { connect, connectAndMerge } from '../../'
import { state } from 'cerebral/tags'

export const Running: React.ComponentClass<any> = connect({
  composing: state`composer.$running`,
  error: state`composer.$error`,
})(function Running({ composing, error }: any) {
  return React.createElement('div', {}, 'hello')
})

export const Running2: React.ComponentClass<any> = connectAndMerge({
  composing: state`composer.$running`,
  error: state`composer.$error`,
})((deps, props, get) => {
  return {
    test: deps.error,
  }
})(function Running2({ test }) {
  return React.createElement('div', {}, 'hello')
})
