export interface StringRoutes {
  [ key: string ]: string | StringRoutes
}

export interface RouteMap {
  [ key: string ]: any
}

export interface ObjectRoute {
  path: string
  signal?: string

  // EXPERIMENTAL: NOT PART OF OFFICIAL API
  map?: RouteMap
  rmap?: RouteMap
}

export type ArrayRoutes = ObjectRoute []

export type Routes = StringRoutes | ArrayRoutes

export interface RouterOptions {
  allowEscape?: boolean
  baseUrl?: string
  onlyHash?: boolean
  preventAutostart?: boolean
  query?: boolean
  routes: Routes
}

export default function Router(opts: RouterOptions): any