  describe('Compute', () => {
    it('should allow use of Compute as state dependency and access props', () => {
      const rootModule = Module({
        state: {
          foo: (get) => get(props`foo`) + 'baz',
        },
      })
      const testProps = {
        foo: 'bar',
      }
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          foo: state`foo`,
        },
        props: testProps,
        controller,
      })
      view.mount()
      assert.deepEqual(view.dependencyMap, {})
      view.render(testProps, (props) => {
        assert.equal(props.foo, 'barbaz')
      })
    })
    it('should have access to state through state argument', () => {
      const rootModule = Module({
        state: {
          foo: 'bar',
          fooComputed: (get) => get(state`foo`),
        },
      })
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          foo: state`fooComputed`,
        },
        controller,
      })
      view.mount()
      assert.deepEqual(view.dependencyMap, {})
      view.render({}, (componentProps) => {
        assert.equal(componentProps.foo, 'bar')
      })
    })
    it('should track state', () => {
      let renderCount = 0
      const rootModule = Module({
        state: {
          foo: 'bar',
          fooComputed: (get) => get(state`foo`),
        },
        sequences: {
          methodCalled: [({ store }) => store.set(state`foo`, 'bar2')],
        },
      })
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          foo: state`fooComputed`,
        },
        controller,
        onUpdate: (stateChanges, force) => {
          assert.equal(stateChanges.length, 1)
          assert.deepEqual(stateChanges[0].path, ['foo'])
          assert.equal(force, undefined)
          view.updateFromState(stateChanges, null, force)
          assert.deepEqual(view.dependencyMap, {})
          renderCount++
        },
      })
      view.mount()
      assert.equal(renderCount, 0)
      assert.deepEqual(view.dependencyMap, {})
      view.render({}, (componentProps) => {
        assert.equal(componentProps.foo, 'bar')
      })
      controller.getSequence('methodCalled')()
      view.render({}, (componentProps) => {
        assert.equal(componentProps.foo, 'bar2')
      })
      assert.equal(renderCount, 1)
    })
    it('should update dependency map when Compute is rerun', () => {
      let renderCount = 0
      const rootModule = Module({
        state: {
          map: {},
          foo: (get) =>
            get(state`map.*`).filter((key) => get(state`map.${key}.awesome`)),
        },
        sequences: {
          changeState: [
            ({ store }) => store.set(state`map.1`, { awesome: true }),
          ],
          changeState2: [({ store }) => store.set(state`map.1.awesome`, false)],
        },
      })
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          foo: state`foo`,
        },
        controller,
        onUpdate: (stateChanges, force) => {
          if (!renderCount) {
            assert.equal(stateChanges.length, 1)
            assert.deepEqual(stateChanges[0].path, ['map', '1'])
            assert.equal(force, undefined)
            view.updateFromState(stateChanges, null, force)
            assert.deepEqual(view.dependencyMap, {})
            renderCount++
          } else {
            assert.equal(stateChanges.length, 1)
            assert.deepEqual(stateChanges[0].path, ['map', '1', 'awesome'])
            assert.equal(force, undefined)
            view.updateFromState(stateChanges, null, force)
            assert.deepEqual(view.dependencyMap, {})
            renderCount++
          }
        },
      })
      view.mount()
      assert.equal(renderCount, 0)
      assert.deepEqual(view.dependencyMap, {})

      view.render({}, (componentProps) => {
        assert.deepEqual(componentProps.foo, [])
      })
      view.controller.getSequence('changeState')()
      view.render({}, (componentProps) => {
        assert.equal(componentProps.foo, '1')
      })
      assert.equal(renderCount, 1)
      view.controller.getSequence('changeState2')()
      view.render({}, (componentProps) => {
        assert.deepEqual(componentProps.foo, [])
      })
      assert.equal(renderCount, 2)
    })
    it('should update dependency map when nested Compute is rerun', () => {
      let renderCount = 0
      const rootModule = Module({
        state: {
          foo: (get) => ({
            nested: get(state`bar`) + 1,
          }),
          bar: 2,
        },
        sequences: {
          changeState: [({ store }) => store.set(state`bar`, 3)],
        },
      })
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          foo: state`foo.nested`,
        },
        controller,
        onUpdate: (stateChanges, force) => {
          view.updateFromState(stateChanges, null, force)
          renderCount++
        },
      })
      view.mount()
      assert.equal(renderCount, 0)
      view.render({}, (componentProps) => {
        assert.equal(componentProps.foo, 3)
      })
      view.controller.getSequence('changeState')()
      view.render({}, (componentProps) => {
        assert.equal(componentProps.foo, 4)
      })
      assert.equal(renderCount, 1)
    })
    it('should handle complex state changes', () => {
      let renderCount = 0
      const rootModule = Module({
        state: {
          user: {
            projects: ['0', '1'],
          },
          projects: {
            '0': 'foo',
            '1': 'bar',
            '2': 'baz',
          },
          userProjects: (get) =>
            get(state`user.projects`).map((projectKey) =>
              get(state`projects.${projectKey}`)
            ),
        },
        sequences: {
          changeState: [({ store }) => store.push(state`user.projects`, '2')],
        },
      })
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          projects: state`userProjects`,
        },
        controller,
        onUpdate: (stateChanges, force) => {
          assert.equal(stateChanges.length, 1)
          assert.deepEqual(stateChanges[0].path, ['user', 'projects'])
          assert.equal(force, undefined)
          view.updateFromState(stateChanges, null, force)
          assert.deepEqual(view.dependencyMap, {})
          renderCount++
        },
      })
      view.mount()
      assert.equal(renderCount, 0)
      assert.deepEqual(view.dependencyMap, {})
      view.render({}, (componentProps) => {
        assert.deepEqual(componentProps.projects, ['foo', 'bar'])
      })
      view.controller.getSequence('changeState')()
      view.render({}, (componentProps) => {
        assert.deepEqual(componentProps.projects, ['foo', 'bar', 'baz'])
      })
      assert.equal(renderCount, 1)
    })
    it('should handle strict deps', () => {
      let renderCount = 0
      const rootModule = Module({
        state: {
          user: {
            projects: ['0', '1'],
          },
          projects: {
            '0': { title: '0' },
            '1': { title: '1' },
          },
          userProjects: (get) =>
            get(state`user.projects`).map((projectKey) =>
              get(state`projects.${projectKey}`)
            ),
        },
        sequences: {
          changeState: [
            ({ store }) => store.set(state`projects.1.title`, 'woop'),
          ],
        },
      })
      const controller = Controller(rootModule)
      const view = new View({
        dependencies: {
          projects: state`userProjects`,
        },
        controller,
        onUpdate: (stateChanges, force) => {
          assert.equal(stateChanges.length, 1)
          assert.deepEqual(stateChanges[0].path, ['projects', '1', 'title'])
          assert.equal(force, undefined)
          view.updateFromState(stateChanges, null, force)
          renderCount++
        },
      })
      view.mount()
      assert.equal(renderCount, 0)
      view.render({}, (componentProps) => {
        assert.deepEqual(componentProps.projects, [
          { title: '0' },
          { title: '1' },
        ])
      })
      view.controller.getSequence('changeState')()
      view.render({}, (componentProps) => {
        assert.deepEqual(componentProps.projects, [
          { title: '0' },
          { title: 'woop' },
        ])
      })
      assert.equal(renderCount, 1)
    })
  })
  it('should handle props updating state deps', () => {
    const rootModule = Module({
      state: {
        foo: 'bar',
        bar: 'baz',
      },
      sequences: {
        changeState: [({ store }) => store.set(state`bar`, 'baz2')],
      },
    })
    const controller = Controller(rootModule)
    const view = new View({
      dependencies: {
        foo: state`${props`path`}`,
      },
      controller,
      props: {
        path: 'foo',
      },
    })
    view.mount()
    assert.deepEqual(view.dependencyMap, { foo: true })
    view.render(
      {
        path: 'foo',
      },
      (componentProps) => {
        assert.equal(componentProps.foo, 'bar')
      }
    )
    view.onPropsUpdate(
      {
        path: 'foo',
      },
      {
        path: 'bar',
      }
    )
    assert.deepEqual(view.dependencyMap, { bar: true })
    view.render(
      {
        path: 'bar',
      },
      (componentProps) => {
        assert.equal(componentProps.foo, 'baz')
      }
    )
    view.controller.getSequence('changeState')()
    view.render(
      {
        path: 'bar',
      },
      (componentProps) => {
        assert.equal(componentProps.foo, 'baz2')
      }
    )
  })
  it('should handle props composition updating value', () => {
    const rootModule = Module({
      state: {
        field: {
          value: '',
        },
      },
      sequences: {
        changeState: [
          ({ store }) => {
            store.set(state`field.value`, 'foo')
            store.merge(state`field`, { mip: 'mop' })
          },
        ],
      },
    })
    const controller = Controller(rootModule)
    const view = new View({
      dependencies: {
        field: state`${props`path`}`,
      },
      controller,
      props: {
        path: 'field',
      },
    })
    view.mount()
    assert.deepEqual(view.dependencyMap, { 'field.**': true })
    view.render(
      {
        path: 'field',
      },
      (componentProps) => {
        assert.deepEqual(componentProps.field, {
          value: '',
        })
      }
    )
    view.controller.getSequence('changeState')()
    view.render(
      {
        path: 'field',
      },
      (componentProps) => {
        assert.deepEqual(componentProps.field, {
          value: 'foo',
          mip: 'mop',
        })
      }
    )
  })
  it('should warn if prop is from the Cerebral state tree when initializing', () => {
    let warnCount = 0
    const originWarn = console.warn
    console.warn = function(...args) {
      warnCount++
      assert.equal(
        args[0],
        'You are passing an array to the component "Test" on prop "list" which is from the Cerebral state tree. You should not do this, but rather connect it directly to this component. This will optimize the component and avoid any rerender issues.'
      )
      originWarn.apply(this, args)
    }
    const rootModule = Module({
      state: {
        foo: 'bar',
        list: [],
      },
    })
    const controller = Controller(rootModule, {
      devtools: {
        warnStateProps: true,
        preventExternalstores: true,
        init() {},
        send() {},
        updateWatchMap() {},
        updateComputedMap() {},
        sendWatchMap() {},
      },
    })
    const view = new View({
      dependencies: {
        foo: state`foo`,
      },
      controller,
      props: {
        list: controller.getState('list'),
      },
      displayName: 'Test',
    })
    assert.equal(view._verifyPropsWarned, true)
    assert.equal(warnCount, 1)
    console.warn = originWarn
  })
  it('should warn if prop is from the Cerebral state tree when updating props', () => {
    let warnCount = 0
    const originWarn = console.warn
    console.warn = function(...args) {
      warnCount++
      assert.equal(
        args[0],
        'You are passing an object to the component "Test" on prop "list" which is from the Cerebral state tree. You should not do this, but rather connect it directly to this component. This will optimize the component and avoid any rerender issues.'
      )
      originWarn.apply(this, args)
    }
    const rootModule = Module({
      state: {
        foo: 'bar',
        list: {},
      },
    })
    const controller = Controller(rootModule, {
      devtools: {
        warnStateProps: true,
        preventExternalstores: true,
        init() {},
        send() {},
        updateWatchMap() {},
        updateComputedMap() {},
        sendWatchMap() {},
      },
    })
    const view = new View({
      dependencies: {
        foo: state`foo`,
      },
      controller,
      props: {
        list: '',
      },
      displayName: 'Test',
    })
    view.mount()
    assert.equal(view._verifyPropsWarned, false)
    view.onPropsUpdate(
      {
        list: '',
      },
      {
        list: controller.getState('list'),
      }
    )
    assert.equal(view._verifyPropsWarned, true)
    assert.equal(warnCount, 1)
    console.warn = originWarn
    console.log('done test 2')
  })
