/* eslint-env mocha */
import { props, state } from '../tags'

import App from '../'
import assert from 'assert'
import { merge } from './'

describe('factory.merge', () => {
  it('should merge value in model', () => {
    const rootModule = {
      state: {
        users: {
          john: 'John Difool',
        },
      },
      sequences: {
        test: [merge(state`users`, { largo: 'Largo Winch' })],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')()
    assert.deepEqual(controller.getState(), {
      users: {
        john: 'John Difool',
        largo: 'Largo Winch',
      },
    })
  })
  it('should merge value from props in model', () => {
    const rootModule = {
      state: {
        users: {
          john: 'John Difool',
        },
      },
      sequences: {
        test: [merge(state`users`, props`value`)],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')({ value: { largo: 'Largo Winch' } })
    assert.deepEqual(controller.getState(), {
      users: {
        john: 'John Difool',
        largo: 'Largo Winch',
      },
    })
  })
  it('should throw on bad argument', (done) => {
    const rootModule = {
      state: {},
      sequences: {
        test: [merge(props`users`, { joe: 'Joe' })],
      },
    }
    const controller = App(rootModule)

    controller.removeListener('error')
    controller.once('error', (error) => {
      assert.ok(error)
      done()
    })

    controller.getSequence('test')()
  })
  it('should create object if no value', () => {
    const rootModule = {
      state: {},
      sequences: {
        test: [merge(state`users`, { joe: 'Joe' })],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')()
    assert.deepEqual(controller.getState(), { users: { joe: 'Joe' } })
  })
  it('should merge multiple objects', () => {
    const rootModule = {
      state: {},
      sequences: {
        test: [
          merge(state`users`, { joe: 'Joe' }, props`extend`, {
            bob: props`bob`,
          }),
        ],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')({
      extend: {
        jack: 'Jack',
      },
      bob: 'Bob',
    })
    assert.deepEqual(controller.getState(), {
      users: {
        joe: 'Joe',
        jack: 'Jack',
        bob: 'Bob',
      },
    })
  })
})
