/* eslint-env mocha */
import { props, state } from '../tags'

import App from '../'
import assert from 'assert'
import { splice } from './'

describe('factory.splice', () => {
  it('should splice value in model', () => {
    const rootModule = {
      state: {
        list: ['a', 'b', 'c', 'd'],
      },
      sequences: {
        test: [splice(state`list`, 1, 2, 'x', 'y')],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')()
    assert.deepStrictEqual(controller.getState(), { list: ['a', 'x', 'y', 'd'] })
  })
  it('should splice value from props in model', () => {
    const rootModule = {
      state: {
        list: ['a', 'b', 'c', 'd'],
      },
      sequences: {
        test: [splice(state`list`, props`idx`, 1, props`x`, props`y`)],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')({ idx: 2, x: 'one', y: 'two' })
    assert.deepStrictEqual(controller.getState(), {
      list: ['a', 'b', 'one', 'two', 'd'],
    })
  })
  it('should throw on bad argument', (done) => {
    const rootModule = {
      state: {},
      sequences: {
        test: [splice(props`list`, 1, 1, 'bar')],
      },
    }
    const controller = App(rootModule)

    controller.removeListener('error')
    controller.once('error', (error) => {
      assert.ok(error)
      done()
    })

    controller.getSequence('test')()
  })
})
