/* eslint-env mocha */
import { parallel, wait } from './'

import App from '../'
import assert from 'assert'

describe('factory.wait', () => {
  it('should hold execution for set time', (done) => {
    const start = Date.now()
    const rootModule = {
      sequences: {
        test: [
          wait(100),
          () => {
            assert.ok(Date.now() - start >= 100)
            done()
          },
        ],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')()
  })
  it('should hold execution for set time in parallel', (done) => {
    const start = Date.now()
    const rootModule = {
      sequences: {
        test: [
          parallel([
            wait(100),
            {
              continue: [
                () => {
                  assert.ok(Date.now() - start >= 100)
                  done()
                },
              ],
            },
          ]),
        ],
      },
    }
    const controller = App(rootModule)
    controller.getSequence('test')()
  })
})
