/* eslint-env mocha */
import { Controller, Module } from '../'
import assert from 'assert'
import { shift } from './'
import { props, state } from '../tags'

describe('operator.shift', () => {
  it('should shift value in model', () => {
    const rootModule = Module({
      state: {
        list: ['a', 'b', 'c'],
      },
      signals: {
        test: [shift(state`list`)],
      },
    })
    const controller = Controller(rootModule)
    controller.getSignal('test')()
    assert.deepEqual(controller.getState(), { list: ['b', 'c'] })
  })
  it('should throw on bad argument', (done) => {
    const rootModule = Module({
      state: {},
      signals: {
        test: [shift(props`list`, 'bar')],
      },
    })
    const controller = Controller(rootModule)

    controller.removeListener('error')
    controller.once('error', (error) => {
      assert.ok(error)
      done()
    })

    controller.getSignal('test')()
  })
})
