interface TagOptions {
  hasValue?: boolean
  isStateDependency?: boolean
}

export class Tag {
  constructor(
    tag: string,
    options: TagOptions,
    strings: string[],
    values: any[]
  )
  tag: string
  options: TagOptions
  strings: string[]
  values: any[]
  getPath(getters: any): string
  getValue(getters: any): any
}

export function props(path: TemplateStringsArray, ...values: any[]): Tag
export function signal(path: TemplateStringsArray, ...values: any[]): Tag
export function state(path: TemplateStringsArray, ...values: any[]): Tag
export function string(path: TemplateStringsArray, ...values: any[]): Tag