import { Module } from '../..';
import { set } from '../../operators';
import { props, state } from '../../tags';

export const module1: Module = {
    state: { foo: 'bar' },
    signals: {
      foo: [set(state`bar`, props`foo`)]
    }
  }
  
export const module2: Module = (module) => {
    module.name = 'module2'
    return {
        state: {
            foo: 'bar'
        }
    }
}